import React, { Component } from "react";

class Form extends Component {
    constructor(props){
        super(props)
        this.state = {
            title : "",
            globalTitle : ""
        };
    };


    render() {
        handleChange = (event) => {
            this.title = event.target.value;
        }
        return (
            <form action="">
                <label htmlFor=""> Title form </label>
                <input type="text" value={this.setState.title} onChange={handleChange}/> 
                <button>Je suis un button</button>
            </form>
        )
    }
};


export default Form;
