import React, { Component , Fragment } from "react";

class Form extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: "",
            globalTitle: ""
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange = (event) => {
        this.setState({
            title: event.target.value,
            globalTitle : `Mon formulaire ${this.state.title}`
        })
    }

    handleSubmit = (event) => {
        this.setState({
            globalTitle: event.target.value
        })
    }

    componentDidMount () {
        console.log("Formulaire rendu")
    }

    componentDidUpdate () {
        console.log("Titre changé")
    }

    render() {
        return (
            <Fragment>
                <h1>{this.state.title}{this.handleChange}</h1>
                <form action="">
                    <label htmlFor=""> Title form
                <input type="text" value={this.state.title} onChange={this.handleChange} />
                    </label>
                    <button type="submit" value="Submit" onSubmit={this.handleChange.globalTitle}>Submit</button>
                </form>
            </Fragment>
        )
    }
};


export default Form;
