import React, { Component , Fragment } from "react";

class Form extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: "Je suis un texte",
            globalTitle: ""
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange = (event) => {
        this.setState({
            title: event.target.value,
        })
    }

    handleSubmit = (event) => {
        this.setState({
            globalTitle: event.target.value
        })
        console.log('Titre Changé')
    }
    render() {
        return (
            <Fragment>
                <h1 value={this.state.globalTitle}>{this.state.globalTitle}{this.handleSubmit}</h1>
                <form action="">
                    <label htmlFor=""> Title form
                <input type="text" value={this.state.title} onChange={this.handleChange} />
                    </label>
                    <button type="submit" value="Submit" onChange={this.state.globalTitle}>Mon formulaire {this.state.title}</button>
                </form>
            </Fragment>
        )
    }
};
console.log("Formulaire rendu")


export default Form;
